import * as React from 'react';

declare type AutoCompleteBorderType = 'tile' | 'rounded' | 'smooth';
declare type AutoCompleteColor = 'primary' | 'info' | 'success' | 'error';

declare interface AutoCompleteProps {
    items: string[] | any[],
    iconKey?: string,
    itemKey?: string,
    avatarKey?: string,
    avatarSize?: string,
    avatarBorderType?: 'rounded',
    contentMaxHeight?: string | number,
    hover?: boolean,
    listHeader?: React.ReactNode | string | number,
    footer?: React.ReactNode | string | number,
    size?: 'medium' | 'large',
    color?: AutoCompleteColor,
    borderType?: AutoCompleteBorderType,
    label?: string,
    placeholder?: string,
    onItemClick?: Function,
    clearable?: boolean,
    onClear?: Function,
    prefix?: React.ReactNode | string | number,
    suffix?: React.ReactNode | string | number,
    whiteBackground?: boolean,
    uppercase?: boolean,
    lifted?: boolean,
    disabled?: boolean,
    required?: boolean,
    readOnly?: boolean,
    dark?: boolean,
    loading?: boolean,
    style?: React.CSSProperties,
    className?: string
}

declare const AutoComplete: React.FC<AutoCompleteProps>;