import * as React from 'react';

declare interface Crumb {
    item: string,
    url?: string,
    icon?: string
}

declare type CrumbSize = 'medium' | 'large';
declare type CrumbColor = 'primary' | 'info' | 'success' | 'error' | 'dark' | 'secondary';
declare type CrumbPosition = 'center' | 'right';

declare interface BreadcrumbsProps {
    crumbs: string[] | Crumb[],
    icon?: string,
    itemKey?: string,
    urlKey?: string,
    isActive?: Function,
    onChange?: Function,
    delimiter?: string,
    size?: CrumbSize,
    color?: CrumbColor,
    position?: CrumbPosition,
    className?: string
}

declare const Breadcrumbs: React.FC<BreadcrumbsProps>;