import * as React from "react";

declare type ButtonColor = 'primary' | 'info' | 'success' | 'error' | 'black' | 'secondary';
declare type ButtonSize = 'medium' | 'large';
declare type BorderType = 'tile' | 'smooth' | 'rounded';
declare type ButtonType = 'button' | 'submit' | 'reset';

declare interface ButtonProps {
    name?: string,
    onClick?: Function,
    lifted?: boolean,
    loading?: boolean,
    uppercase?: boolean,
    borderType?: BorderType,
    color?: ButtonColor,
    light?: boolean,
    dark?: boolean,
    icon?: React.ReactNode,
    disabled?: boolean,
    size?: ButtonSize,
    block?: boolean,
    outlined?: boolean,
    iconAllotted?: boolean,
    iconLeft?: boolean,
    btnRef?: React.RefObject<any>,
    type?: ButtonType,
    className?: string
}
declare const Button: React.FC<ButtonProps>;