import * as React from 'react';

declare type ButtonSize = 'medium' | 'large';
declare type ButtonColor = 'primary' | 'info' | 'success' | 'error' | 'secondary' | 'black';

declare interface ButtonGroupProps {
    default?: number,
    onChange?: Function,
    options: string[],
    lifted?: boolean,
    outlined?: boolean,
    size?: ButtonSize,
    name: string,
    icon?: boolean,
    color?: ButtonColor,
    className?: string
}

declare const ButtonGroup: React.FC<ButtonGroupProps>;