import * as React from 'react';

declare type CalendarLocale = 'en' | 'ru' | 'kz' | 'fr';
declare interface EventItem {
    title?: string,
    event: string,
    color?: string
}
declare interface CalendarEvent {
    date: Date | string,
    items: EventItem[]
}
declare interface CalendarHoliday {
    date: Date | string
}

declare interface CalendarProps {
    locale?: CalendarLocale,
    active?: Date,
    minDate?: Date,
    maxDate?: Date,
    disabledDates?: Date[],
    events?: CalendarEvent[],
    holidays?: CalendarHoliday[],
    onDate?: Function,
    selectBorderType?: 'tile' | 'rounded' | 'smooth',
    hideWeekend?: boolean,
    hideCurrentDay?: boolean,
    color?: 'primary' | 'info' | 'success' | 'error' | 'dark',
    size?: 'medium' | 'large',
    limit?: number,
    onlyPast?: boolean,
    weekStartsSunday?: boolean,
    shortWeekName?: boolean,
    dark?: boolean,
    width?: number | string,
    popOverTrigger?: 'hover' | 'click',
    className?: string
}

declare const Calendar: React.FC<CalendarProps>;
