import * as React from 'react';

declare interface CarouselSlide {
    img: string, 
    title: string, 
    [key: string]: any
}

declare interface CarouselProps {
    items: CarouselSlide[],
    width?: string | number,
    height?: string | number,
    autoMotion?: boolean,
    hideArrows?: boolean,
    hideDots?: boolean,
    interval?: number,
    onClick?: Function,
    className?: string
}

declare const Carousel: React.FC<CarouselProps>;