import * as React from 'react';

declare type CheckboxColor = 'primary' | 'info' | 'success' | 'error' | 'secondary';
declare type CheckboxSize = 'medium' | 'large';

declare interface CheckboxProps {
    checked: boolean,
    onChange?: Function,
    disabled?: boolean,
    fill?: boolean,
    color?: CheckboxColor,
    size?: CheckboxSize,
    label?: string,
    className?: string
}

declare const Checkbox: React.FC<CheckboxProps>;