import * as React from 'react';

declare type ButtonColor = 'primary' | 'info' | 'success' | 'error' | 'secondary' | 'black';

declare interface DialogProps {
    dark?: boolean,
    onConfirm?: Function,
    onCancel: Function,
    visible: boolean,
    icon?: string,
    title?: string,
    titleColor?: string,
    description?: string,
    confirmOnly?: boolean,
    cancelText?: string,
    cancelBtnColor?: ButtonColor,
    confirmText?: string,
    confirmBtnColor?: ButtonColor
}

declare const Dialog: React.FC<DialogProps>;