import * as React from 'react';

declare type FloatingBtnPosition = 'bottom-right' | 'top-left' | 'top-right' | 'bottom-left';
declare type FloatingBtnColor = 'primary' | 'info' | 'success' | 'error' | 'secondary' | 'black';
declare type FloatingBtnSize = 'medium' | 'large';
declare type TooltipPosition = 'bottom' | 'left' | 'right';

declare interface FloatingBtnProps {
    icon: string,
    position?: FloatingBtnPosition,
    offsetX?: number,
    offsetY?: number,
    lifted?: boolean,
    color?: FloatingBtnColor,
    size?: FloatingBtnSize,
    onClick?: Function,
    dark?: boolean,
    light?: boolean,
    tooltip?: string,
    tooltipPosition?: TooltipPosition
}

declare const FloatingBtn: React.FC<FloatingBtnProps>;