import * as React from 'react';

declare type InputType = 'text' | 'password' | 'number' | 'email';
declare type InputColor = 'primary' | 'info' | 'success' | 'error';
declare type InputBorderType = 'tile' | 'smooth' | 'rounded';
declare type InputSize = 'medium' | 'large';
declare type HintColor = 'success' | 'error';

declare interface InputFieldProps {
    onChange?: Function,
    onKeyUp?: Function,
    onEnter?: Function,
    onFocus?: Function,
    onBlur?: Function,
    value?: string | number,
    type?: InputType,
    required?: boolean,
    readOnly?: boolean,
    autoComplete?: boolean,
    name?: string,
    placeholder?: string,
    clearable?: boolean,
    onClear?: Function,
    lifted?: boolean,
    autoFocus?: boolean,
    color?: InputColor,
    disabled?: boolean,
    borderType?: InputBorderType,
    uppercase?: boolean,
    size?: InputSize,
    prefix?: React.ReactNode,
    suffix?: React.ReactNode,
    min?: number,
    max?: number,
    dark?: boolean,
    maxLength?: number,
    minLenght?: number,
    whiteBackground?: boolean,
    label?: string,
    hint?: string,
    hintColor?: HintColor,
    style?: any,
    className?: string
}

declare const InputField: React.FC<InputFieldProps>;