import * as React from 'react';

declare interface ListProps {
    size?: 'medium' | 'large',
    dark?: boolean,
    grid?: boolean,
    header?: React.ReactNode | string | number,
    Item?: React.FC<ListItemProps>,
    className?: string,
    id?: string,
    hover?: boolean,
    children: React.ReactNode
}

declare interface ListItemProps {
    roundedActive?: boolean,
    leftBorder?: boolean,
    isActiveItem?: boolean | Function,
    color?: 'primary' | 'info' | 'success' | 'error',
    hover?: boolean,
    noDivider?: boolean,
    checkbox?: boolean,
    item?: string | object | number,
    onClick?: Function,
    onMouseOver?: Function,
    onMouseLeave?: Function,
    subTitle?: string,
    href?: string,
    blank?: boolean,
    icon?: string,
    tooltip?: string,
    tooltipPosition?: 'bottom' | 'right' | 'left',
    avatar?: React.ReactNode,
    controls?: React.ReactNode,
    className?: string
}

declare interface ListObject extends React.FC<ListProps> {
    Item: React.FC<ListItemProps>
}

declare const List: ListObject;