import * as React from 'react';

declare type PopOverTrigger = 'hover' | 'click';
declare type PopOverPosition = 'bottom' | 'left' | 'right';

declare interface PopOverProps {
    title?: string,
    content?: string,
    control?: boolean,
    onClose?: Function,
    trigger?: PopOverTrigger,
    dark?: boolean,
    noBlur?: boolean,
    position?: PopOverPosition,
    className?: string
}

declare const PopOver: React.FC<PopOverProps>;