import * as React from 'react';

declare type ProgressColor = 'primary' | 'info' | 'success' | 'error' | 'dark';
declare type ProgressSize = 'medium' | 'large';

declare interface ProgressProps {
    complete: number,
    circle?: boolean,
    active?: boolean,
    color?: ProgressColor,
    disabled?: boolean,
    size?: ProgressSize,
    icon?: string,
    iconSize?: number,
    iconColor?: string,
    radius?: number,
    stroke?: number,
    hideState?: boolean,
    dark?: boolean,
    className?: string
}

declare const Progress: React.FC<ProgressProps>;