import * as React from 'react';

declare type RadioGroupColor = 'primary' | 'info' | 'success' | 'error';
declare type RadioGroupSize = 'medium' | 'large';

declare interface RadioGroupProps {
    options: string[],
    disabled?: boolean,
    color?: RadioGroupColor,
    size?: RadioGroupSize,
    value: string | number,
    name: string,
    labelName?: string,
    onChange?: Function,
    vertical?: boolean,
    className?: string
}

declare const RadioGroup: React.FC<RadioGroupProps>;