import * as React from 'react';

declare type SelectBorderType = 'tile' | 'rounded' | 'smooth';
declare type SelectSize = 'medium' | 'large';
declare type SelectColor = 'primary' | 'info' | 'success' | 'error';
declare type TagColor = 'secondary' | 'primary' | 'info' | 'success' | 'error' | 'dark' | 'yellow';

declare interface SelectProps {
    items: any[],
    itemKey?: string,
    value: string | object,
    onChange?: Function,
    onSelect?: Function,
    returnObject?: boolean,
    prefix?: React.ReactNode | string | number,
    searchable?: boolean,
    searchPlaceholder?: string,
    borderType?: SelectBorderType,
    multiple?: boolean,
    placeholder?: string,
    size?: SelectSize,
    color?: SelectColor,
    dark?: boolean,
    tagColor?: TagColor,
    tagOutlined?: boolean,
    whiteBackground?: boolean,
    disabled?: boolean,
    width?: string | number,
    maxHeight?: string | number,
    label?: string,
    childrenKey?: string,
    className?: string
}

declare const Select: React.FC<SelectProps>;