import * as React from 'react';

declare type SwitchColor = 'primary' | 'info' | 'success' | 'error';

declare interface SwitchProps {
    leftIcon?: string,
    leftIconColor?: string,
    rightIcon?: string,
    rightIconColor?: string,
    leftLabel?: string,
    rightLabel?: string,
    disabled?: boolean,
    check: boolean,
    onChange: Function,
    vertical?: boolean,
    color?: SwitchColor,
    className?: string 
}

declare const Switch: React.FC<SwitchProps>;