import * as React from 'react';

declare type TableColor = 'primary' | 'info' | 'success' | 'error';
declare type TableAlignment = 'left' | 'center' | 'right';
declare type TablePaginationPosition = 'left' | 'right';
declare type TablePaginationColor = 'primary' | 'info' | 'success' | 'error';

declare interface TableProps {
    id?: string,
    tableRef?: React.RefObject<any>,
    items: any[],
    itemsTotal?: number,
    itemTitles?: string[],
    headers?: string[],
    color?: TableColor,
    perPageVariants?: number[],
    perPageText?: string,
    alignment?: TableAlignment,
    tableTitle?: React.ReactNode,
    searchable?: boolean,
    searchKey?: string,
    pagination?: boolean,
    searchPlaceholder?: string,
    paginationPosition?: TablePaginationPosition,
    paginationColor?: TableColor,
    checkbox?: boolean,
    controls?: React.ReactNode,
    paginationDense?: boolean,
    selectKey?: string,
    onSelect?: Function,
    sortable?: boolean,
    index?: boolean,
    indexSign?: string,
    stripped?: boolean,
    bordered?: boolean,
    grid?: boolean,
    dark?: boolean,
    noHover?: boolean,
    stickyHead?: boolean,
    paginationBorderType?: 'tile' | 'rounded',
    footer?: React.ReactNode,
    className?: string,
    style?: React.CSSProperties
}

declare const Table: React.FC<TableProps>;