import * as React from 'react';

declare interface TableSimpleProps {
    id?: string,
    children?: React.ReactChild,
    stripped?: boolean,
    noHover?: boolean,
    dark?: boolean,
    grid?: boolean,
    bordered?: boolean,
    style?: React.CSSProperties,
    className?: string,
    tableRef: React.RefObject<any>
}

declare interface TableHeadProps {
    children?: React.ReactChild,
    style?: React.CSSProperties,
    className?: string
}

declare interface TableBodyProps {
    children?: React.ReactChild,
    style?: React.CSSProperties,
    className?: string
}

declare interface TableRowProps {
    children?: React.ReactChild,
    style?: React.CSSProperties,
    className?: string
}

declare interface TableCellProps {
    children?: React.ReactChild,
    style?: React.CSSProperties,
    className?: string,
    colSpan?: number,
    rowSpan?: number,
    align?: 'left' | 'right' | 'center'
}

declare interface TableHeaderProps {
    children?: React.ReactChild,
    style?: React.CSSProperties,
    className?: string,
    colSpan?: number,
    rowSpan?: number,
    align?: 'left' | 'right' | 'center'
}

declare interface TableSimpleObject extends React.FC<TableSimpleProps> {
    Head: React.FC<TableHeadProps>,
    Body: React.FC<TableBodyProps>,
    Row: React.FC<TableRowProps>,
    Cell: React.FC<TableCellProps>,
    Header: React.FC<TableHeaderProps>
}

declare const TableSimple: TableSimpleObject;