import * as React from 'react';

declare type TabsPosition = 'left' | 'bottom' | 'right';
declare type TabsSize = 'medium' | 'large';
declare type TabsColor = 'primary' | 'info' | 'error' | 'success';

declare interface TabsProps {
    defaultTab?: number,
    tabs: string[] | object[],
    tabItems: any[],
    contentPadding?: string,
    tabTitleKey?: string,
    tabIconKey?: string,
    centered?: boolean,
    stretch?: boolean,
    position?: TabsPosition,
    size?: TabsSize,
    activeHighlightFill?: boolean,
    onChange?: Function,
    color?: TabsColor,
    className?: string
}

declare const Tabs: React.FC<TabsProps>;