import * as React from 'react';

declare type TagColor = 'secondary' | 'primary' | 'info' | 'success' | 'error' | 'dark' | 'yellow';

declare interface TagProps {
    visible?: boolean,
    uppercase?: boolean,
    outlined?: boolean,
    lifted?: boolean,
    color?: TagColor,
    borderType?: 'tile' | 'rounded' | 'smooth',
    tagRef?: React.RefObject<any>,
    small?: boolean,
    tiny?: boolean,
    children?: React.ReactChild,
    style?: React.CSSProperties,
    onClose?: Function,
    onClick?: Function,
    closable?: boolean,
    className?: string
} 

declare const Tag: React.FC<TagProps>;