import * as React from 'react';

declare type HintColor = 'success' | 'error';
declare type TextareaFieldColor = 'primary' | 'info' | 'success' | 'error';
declare type TextareaFieldBorderType = 'tile' | 'smooth';

declare interface TextareaFieldProps {
    whiteBackground?: boolean,
    value: string,
    onChange?: Function,
    required?: boolean,
    onKeyUp?: Function,
    placeholder?: string,
    label?: string,
    hint?: string,
    hintColor?: HintColor,
    readOnly?: boolean,
    color?: TextareaFieldColor,
    borderType?: TextareaFieldBorderType,
    rows?: number,
    maxRows?: number,
    width?: string | number,
    prefix?: React.ReactNode | string | number,
    suffix?: React.ReactNode | string | number,
    autoFocus?: boolean,
    onClear?: Function,
    dark?: boolean,
    clearable?: boolean,
    onBlur?: Function,
    disabled?: boolean,
    onFocus?: Function,
    lifted?: boolean,
    className?: string
}

declare const TextareaField: React.FC<TextareaFieldProps>;