import * as React from 'react';

declare type TimelineColor = 'primary' | 'info' | 'success' | 'error' | 'yellow' | 'dark';

declare interface TimelineProps {
    items: any[],
    centered?: boolean,
    title?: string,
    subTitle?: string,
    flatCard?: boolean,
    icon?: string,
    color?: TimelineColor,
    data: React.ReactNode,
    tagBorderType?: 'tile' | 'rounded' | 'smooth',
    tagColor?: TimelineColor,
    tagOutlined?: boolean,
    date?: string,
    className?: string
}

declare const Timeline: React.FC<TimelineProps>;