import * as React from 'react';

declare type UploaderBorderType = 'rounded' | 'smooth' | 'tile';
declare type UploaderColor = 'primary' | 'info' | 'success' | 'error';

declare interface UploaderProps {
    value?: object[],
    borderType?: UploaderBorderType,
    color?: UploaderColor,
    light?: boolean,
    dark?: boolean,
    label?: string,
    lifted?: boolean,
    disabled?: boolean,
    required?: boolean,
    title?: string,
    accept?: string,
    multiple?: boolean,
    onChange?: Function,
    placeholder?: string,
    width?: number | string,
    height?: number | string,
    onBlur?: Function,
    onFocus?: Function,
    onDelete?: Function,
    dropBox?: boolean,
    className?: string
}

declare const Uploader: React.FC<UploaderProps>;